const content = $input.all()[0].json.output

let jsonString = content.match(/{[\s\S]*}/)[0];
let jsonObject = JSON.parse(jsonString);

jsonObject.description = jsonObject.description.replace(/\n/g, '<br>');

// Replace ** with <strong> tags for bold text
jsonObject.description = jsonObject.description.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');

// Replace * with <em> tags for italic text
jsonObject.description = jsonObject.description.replace(/\*(.*?)\*/g, '<em>$1</em>');
console.log(jsonString)

// Parse the JSON string into an object


return jsonObject;

