const idTicketQuery = $node['Trigger'].json.query.trim() ? $node['Trigger'].json.query.trim() : null
console.log('sss', idTicketQuery)

const listTicket =  $input.all()[0].json.data
console.log('arg', listTicket)
let ticketSupport

for(const item of listTicket){
  if(item.ticket_number.includes(idTicketQuery)){
    ticketSupport = item
    break
  }
}
return {
  ticketSupport: ticketSupport
};

const listTickets = $input.all()[0]?.json?.data
const InfoTicket = $node['Get Ticket ID'].json.ticketSupport

let document =''
document+='List Tickets comment sent by client with admin: \n'
for (const item of listTickets.reverse()) {
  document+= `${item?.user?.id == InfoTicket.requester.id ? 'Client ' + item?.user?.name : item?.user?.id == InfoTicket.agent.id ? 'Agent ' + item?.user?.name : 'Admin ' + item?.user?.name} Send:  ${item?.message} \nTime Send: ${item?.created_at} \n\n`
}
console.log(document)

return {messagesSent: document};

let message =  $input.all()[0].json.output 
message = message.replace(/\*/g, '')
message = message.replace(/\n\n/g, '\\n\\n');
message = message.replace(/\n/g, '\\n');
message = message.replace(/"/g, '');
return {
  output: message
};

