let obj = []; // Khởi tạo mảng rỗng để lưu trữ kết quả

for (const item of $input.all()) {
  // Kiểm tra nếu custom_fields_data tồn tại
  if (item?.json?.data?.custom_fields_data) {
    // Tìm các đối tượng không có giá trị bắt đầu bằng 'http' hoặc 'www' hoặc kết thúc bằng '.com'
    const filteredItems = item.json.data.custom_fields_data.filter(i => 
      i?.value && 
      !i.value.startsWith('http') && 
      !i.value.startsWith('www') && 
      !i.value.endsWith('.com')
    );

    // Nếu có đối tượng nào không bắt đầu bằng 'http', 'www' hoặc không kết thúc bằng '.com', thêm vào mảng obj
    if (filteredItems.length > 0) {
      obj.push(item); // Thêm item vào mảng
    }
  }
}

console.log('item.json.name:', obj); // In ra mảng kết quả

return obj; // Trả về mảng các đối tượng

const data = $('Cloodo Project11').item.json.data.description;
const uuidPattern = /\b[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}\b/i;
const uuidMatch = uuidPattern.exec(data);

$input.item.json.myNewField = uuidMatch ? uuidMatch[0] : null;

return $input.item;

let obj = []; // Khởi tạo mảng rỗng để lưu trữ kết quả

for (const item of $input.all()) {
  // Tìm các đối tượng trong custom_fields_data có label là 'Website'
  const foundItem = item?.json?.data?.custom_fields_data.find(i => i?.label === 'Website');

  // Kiểm tra nếu foundItem tồn tại và không có giá trị
  if (!foundItem || !foundItem.value) {
    // Nếu tìm thấy và không có giá trị, thêm vào mảng obj
    obj.push(item); // Thêm đối tượng gốc vào mảng
  }

  console.log('found:', foundItem); // In ra giá trị tìm thấy
}

return obj; // Trả về mảng các đối tượng chưa có giá trị cho Website
