for (const item of $input.all()) {
  let email = item?.json?.email;  // Lấy email từ item

  // Kiểm tra nếu email là một chuỗi chứa nhiều email (tách các email bằng dấu phẩy)
  if (email) {
    // Tách chuỗi email thành mảng nếu có dấu phẩy
    const emailArray = email.split(',').map(e => e.trim());

    // Lọc ra các email không có đuôi @cmsmart.net và giữ lại những email còn lại
    const validEmails = emailArray.filter(e => !(e.endsWith('@cmsmart.net') || e.endsWith('@netbasejsc.com') || e === 'perlanguyen.ba@gmail.com'));

    // Nếu có email hợp lệ còn lại, gán vào item.json.emailClient
    if (validEmails.length > 0) {
      item.json.emailClient = validEmails.join(', ');  // Gán lại các email còn lại (nếu có)
    } else {
      // Nếu không có email hợp lệ, gán giá trị từ email_client_facebook
      item.json.emailClient = item?.json?.email_client_facebook || null;
    }
  } else {
    // Nếu không có email, gán giá trị từ email_client_facebook
    item.json.emailClient = item?.json?.email_client_facebook || null;
  }
}

return $input.all();

