let obj = []; // Khởi tạo mảng rỗng để lưu trữ kết quả

for (const item of $input.all()) {
  // Tìm các đối tượng trong custom_fields_data có label là 'Website'
  const foundItem = item?.json?.data?.custom_fields_data.find(i => i?.label === 'Website' || '');
  const nameClient = item?.json?.data?.client;
  const projectAdmin = item?.json?.data?.project_admin ? item?.json?.data?.project_admin : item?.json?.data?.members[0];
    // Nếu tìm thấy, gộp foundItem và nameClient vào một đối tượng
    obj.push({
      website: foundItem, 
      client: nameClient,
      adminProject: projectAdmin
    });

  console.log('found:', foundItem); // In ra mảng kết quả
}

return obj; // Trả về mảng các giá trị

let dataReFormat = ''
const rawData = $input.all()[0]?.json



dataReFormat =  `\n Information of Project: \n Name: ${rawData?.project?.name}; \n Start Date of Project: ${rawData?.project?.start_date}; \n Deadline of Project: ${rawData?.project?.deadline};`


dataReFormat+='\n \n Assigned team members of project:\n '
if(Array.isArray(rawData?.members)){
      for (const [index, item] of rawData?.members?.entries()) {
      dataReFormat += `Member ${index + 1} \n`;
      dataReFormat += `Name: ${item?.name} \n`;
      dataReFormat += `Designation: ${item?.designation?.name} \n`;
    }
  dataReFormat += ` \n \n`;
}else{
      dataReFormat += `Name: ${rawData?.members?.name} \n`;
      dataReFormat += `Email: ${rawData?.members?.email} \n`;
}

dataReFormat = dataReFormat +  '\n \n Contact Account Manager (PM): \n ' + 'Name: ' + rawData?.project?.adminname + '\n Email: ' + rawData?.project?.adminEmail


console.log('dataReFormat: ', dataReFormat)

return { prepareText: dataReFormat };
