const products = items[0].json.data.products;

for (let product of products) {
  if (product.category && product.category[0] && product.category[0].id) {
    return [{ json: { categoryId: product.category[0].id } }];
  }
}

// Return null if no category ID is found
return [{ json: { categoryId: null } }];

