const allItems = Object.values($items()[0].json);
console.log('All items:', allItems);

const selectedItems = $node['Get Feature Posted'].json

const selectedItemsArray = Array.isArray(selectedItems) ? selectedItems : [selectedItems]

const remainingItems = allItems.filter(item=> !selectedItemsArray.some(selected => selected.id === item.id))

if (remainingItems.length === 0) {
    throw new Error('All items have already been selected.');
}

// Chọn ngẫu nhiên một item duy nhất
if (allItems.length > 0) {
  const randomIndex = Math.floor(Math.random() * allItems.length);  // Lấy chỉ số ngẫu nhiên dựa trên độ dài mảng
  const randomItem = remainingItems[randomIndex];  // Lấy item ngẫu nhiên

  // Trả về chỉ một item dưới dạng mảng chứa đối tượng JSON
  return {
    json: randomItem
  };
} else {
  // Trả về thông báo lỗi nếu không có items nào
  return {
    json: { message: "No items found" }
  };
}

const items = $items(); // Get all items from the current input
const selectedItems = $node["Get object posted"].json; // Get previously selected items

// If selectedItems is not an array, wrap it in an array
const selectedItemsArray = Array.isArray(selectedItems) ? selectedItems : [selectedItems];

// Filter out already selected items
const remainingItems = items.filter(item => 
    !selectedItemsArray.some(selected => selected.id === item.json.id)
);

// If no remaining items, throw an error
if (remainingItems.length === 0) {
    throw new Error('All items have already been selected.');
}

// Select a random item from the remaining ones
const randomIndex = Math.floor(Math.random() * remainingItems.length);
const selectedItem = remainingItems[randomIndex];

// Return the randomly selected item
return [selectedItem];

const listItemReFormat = [];

for (const item of $input.all()) {
  const itemData = item?.json

  const objFAQ = {}
  const objFeature = {}
  const objAward = {}
  const objProjectMedia = {}
  const objProjectReview = {}

  if(itemData["['attributes.faqs']"]){
    objFAQ.id = itemData["['attributes.faqs']"].id
    objFAQ.name = itemData["['attributes.faqs']"]?.attributes?.question
    objFAQ.description = itemData["['attributes.faqs']"]?.attributes?.answer
    objFAQ.type = 'faqs'
    objFAQ.obj = 'project'
    objFAQ.parent = itemData.parent || '';  
    objFAQ.id_parent = itemData.id_parent || ''; 
  }

  if(itemData["['attributes.features']"]){
    objFeature.id = itemData["['attributes.features']"].id
    objFeature.name = itemData["['attributes.features']"]?.attributes?.title
    objFeature.description = itemData["['attributes.features']"]?.attributes?.text
    objFeature.type = 'features'
    objFeature.obj = 'project'
    objFeature.parent = itemData.parent || '';  
    objFeature.id_parent = itemData.id_parent || '';  
  }

    if(itemData["['attributes.awards']"]){
    objAward.id = itemData["['attributes.awards']"].id
    objAward.name = itemData["['attributes.awards']"]?.attributes?.name
    objAward.description = itemData["['attributes.awards']"]?.attributes?.highlight
    objAward.type = 'awards'
    objAward.obj = 'project'
  }

  if(itemData["['attributes.project_files']"]){
    objProjectMedia.id = itemData["['attributes.project_files']"].id
    objProjectMedia.name = itemData["['attributes.project_files']"]?.attributes?.file_name
    objProjectMedia.description = itemData["['attributes.project_files']"]?.attributes?.description
    objProjectMedia.type = 'project_files'
    objProjectMedia.obj = 'project'
    objProjectMedia.parent = itemData.parent || '';  
    objProjectMedia.id_parent = itemData.id_parent || '';  
  }

  if(itemData["['attributes.project_reviews']"] && itemData["['attributes.project_reviews']"]?.attributes?.rating === 5){
    objProjectReview.id = itemData["['attributes.project_reviews']"].id
    objProjectReview.name = itemData["['attributes.project_reviews']"]?.attributes?.rating
    objProjectReview.description = itemData["['attributes.project_reviews']"]?.attributes?.comment
    objProjectReview.type = 'project_reviews'
    objProjectReview.obj = 'project'
    objProjectReview.parent = itemData.parent || '';  
    objProjectReview.id_parent = itemData.id_parent || '';  
  }


  objFAQ && objFAQ.id && listItemReFormat.push(objFAQ)
  objAward && objAward.id && listItemReFormat.push(objAward)
  objFeature && objFeature.id&& listItemReFormat.push(objFeature)
  // objProjectMedia && objProjectMedia.id&& listItemReFormat.push(objProjectMedia)
  objProjectReview && objProjectReview.id&& listItemReFormat.push(objProjectReview)
}
const items = $items

return listItemReFormat
